<script nonce="UN1TaH49ucwnUKGWeww+ig==">
  const arrowButton = document.querySelector('#arrow-cta');
  const downArrow = document.querySelector('#down-arrow');
  const upArrow = document.querySelector('#up-arrow');
  const displayNoneClass = 'display-none';

  const calculateScreenMidPoint = () => document.documentElement.clientHeight / 2;
  let screenMidPoint = calculateScreenMidPoint();

  const showDownArrow = () => {
    upArrow.classList.add(displayNoneClass);
    downArrow.classList.remove(displayNoneClass);
  }

  const showUpArrow = () => {
    downArrow.classList.add(displayNoneClass);
    upArrow.classList.remove(displayNoneClass);
  }

  downArrow.addEventListener('click', () => {
    showUpArrow();
  });

  upArrow.addEventListener('click', () => {
    showDownArrow();
  });

  window.addEventListener('scroll', () => {
    const isArrowOnTopHalfOfThePage = arrowButton.getBoundingClientRect().top < screenMidPoint;
    const isUpArrowHidden = upArrow.classList.contains(displayNoneClass);
    const isDownArrowHidden = downArrow.classList.contains(displayNoneClass);

    if (isArrowOnTopHalfOfThePage && isUpArrowHidden) {
      showUpArrow();
    } else if (!isArrowOnTopHalfOfThePage && isDownArrowHidden) {
      showDownArrow();
    }
  });

  window.addEventListener('resize', () => {
    screenMidPoint = calculateScreenMidPoint();
  });
</script>
